import React, { useState, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import { useAuth } from '../AuthContext';

const Header = ({ isAWSMode }) => {
  const { user, logout, isAdmin } = useAuth();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const buttonRef = useRef(null);
  const [dropdownPosition, setDropdownPosition] = useState({ top: 0, right: 0 });

  // Calculate dropdown position based on button position
  useEffect(() => {
    if (showUserMenu && buttonRef.current) {
      const rect = buttonRef.current.getBoundingClientRect();
      setDropdownPosition({
        top: rect.bottom + 8, // 8px gap below button
        right: window.innerWidth - rect.right // Position from right edge
      });
    }
  }, [showUserMenu]);

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
  };

  // Dropdown component to be portaled
  const DropdownMenu = () => (
    <>
      {/* Backdrop overlay */}
      <div 
        className="fixed inset-0 z-[999999]" 
        onClick={() => setShowUserMenu(false)}
      />
      
      {/* Dropdown content */}
      <div 
        className="fixed w-64 bg-white/20 backdrop-blur-lg rounded-lg border border-white/20 shadow-xl z-[9999999]"
        style={{
          top: `${dropdownPosition.top}px`,
          right: `${dropdownPosition.right}px`,
          maxHeight: `${window.innerHeight - dropdownPosition.top - 20}px`,
          overflowY: 'auto'
        }}
      >
        <div className="p-4 border-b border-white/20">
          <p className="text-white font-medium">{user?.name || user?.username}</p>
          <p className="text-purple-300 text-sm">{user?.email}</p>
          <div className="flex items-center space-x-2 mt-2 flex-wrap gap-1">
            <span className={`px-2 py-1 rounded text-xs font-medium ${
              user?.role === 'admin' 
                ? 'bg-purple-500/30 text-purple-200' 
                : 'bg-blue-500/30 text-blue-200'
            }`}>
              {user?.role === 'admin' ? 'üëë Admin' : 'üë§ User'}
            </span>
            {user?.is_verified && (
              <span className="px-2 py-1 rounded text-xs font-medium bg-green-500/30 text-green-200">
                ‚úÖ Verified
              </span>
            )}
            {user?.is_2fa_enabled && (
              <span className="px-2 py-1 rounded text-xs font-medium bg-orange-500/30 text-orange-200">
                üîê 2FA
              </span>
            )}
          </div>
        </div>

        <div className="p-2">
          {/* Profile option */}
          <button 
            onClick={() => {
              alert(`Profile Information:\n\nName: ${user?.name}\nEmail: ${user?.email}\nRole: ${user?.role}\nVerified: ${user?.is_verified ? 'Yes' : 'No'}\n2FA: ${user?.is_2fa_enabled ? 'Enabled' : 'Disabled'}`);
              setShowUserMenu(false);
            }}
            className="w-full text-left px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors"
          >
            üë§ Profile
          </button>

          {/* Upload History */}
          <button 
            onClick={() => {
              alert('üìÅ Upload History\n\nThis feature will show your recent video uploads and processing history. Coming soon!');
              setShowUserMenu(false);
            }}
            className="w-full text-left px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors"
          >
            üìÅ Upload History
          </button>

          {/* Admin Panel (admin only) */}
          {isAdmin() && (
            <button 
              onClick={() => {
                alert('‚öôÔ∏è Admin Panel\n\nAdmin features:\n‚Ä¢ User Management\n‚Ä¢ System Settings\n‚Ä¢ Upload Management\n‚Ä¢ Security Configuration\n\nFull admin panel coming soon!');
                setShowUserMenu(false);
              }}
              className="w-full text-left px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors"
            >
              ‚öôÔ∏è Admin Panel
            </button>
          )}

          {/* Test CORS */}
          <button 
            onClick={async () => {
              try {
                console.log('Testing CORS...');
                const response = await fetch('http://localhost:8001/api/test-cors', {
                  method: 'GET',
                  headers: {
                    'Origin': 'http://localhost:3000',
                    'Content-Type': 'application/json'
                  }
                });
                const data = await response.json();
                console.log('CORS test successful:', data);
                alert('üß™ CORS Test Results:\n\n‚úÖ CORS is working properly!\n‚úÖ Backend communication successful\n‚úÖ Authentication system ready');
              } catch (error) {
                console.error('CORS test failed:', error);
                alert('‚ùå CORS test failed: ' + error.message);
              }
              setShowUserMenu(false);
            }}
            className="w-full text-left px-3 py-2 text-yellow-300 hover:bg-yellow-500/10 rounded-md transition-colors"
          >
            üß™ Test CORS
          </button>

          {/* 2FA Setup */}
          <button 
            onClick={() => {
              if (user?.is_2fa_enabled) {
                alert('üîê 2FA Management\n\n‚úÖ Two-Factor Authentication is currently ENABLED\n\nOptions:\n‚Ä¢ View backup codes\n‚Ä¢ Disable 2FA\n‚Ä¢ Regenerate codes\n\nFull 2FA management coming soon!');
              } else {
                alert('üîê Setup 2FA\n\nüîí Two-Factor Authentication adds an extra layer of security\n\nSetup includes:\n‚Ä¢ QR code for authenticator app\n‚Ä¢ Backup recovery codes\n‚Ä¢ TOTP verification\n\nFull 2FA setup coming soon!');
              }
              setShowUserMenu(false);
            }}
            className="w-full text-left px-3 py-2 text-white hover:bg-white/10 rounded-md transition-colors"
          >
            üîê {user?.is_2fa_enabled ? 'Manage 2FA' : 'Setup 2FA'}
          </button>

          <div className="border-t border-white/20 my-2"></div>

          {/* Logout */}
          <button
            onClick={() => {
              if (confirm('üö™ Sign Out\n\nAre you sure you want to sign out?')) {
                alert('‚úÖ Signed out successfully!\n\nYou will be redirected to the login page.');
                handleLogout();
              }
              setShowUserMenu(false);
            }}
            className="w-full text-left px-3 py-2 text-red-300 hover:bg-red-500/10 rounded-md transition-colors"
          >
            üö™ Sign Out
          </button>
        </div>
      </div>
    </>
  );

  return (
    <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 mb-8 border border-white/20">
      <div className="flex justify-between items-center">
        {/* Left side - Title and AWS indicator */}
        <div>
          <h1 className="text-4xl font-bold text-white mb-2">
            üé¨ Video Splitter Pro
            {isAWSMode && <span className="text-sm text-green-400 block">‚ö° AWS Amplify Mode</span>}
          </h1>
          <p className="text-xl text-purple-200">
            Split videos of any size while preserving subtitles and quality
          </p>
        </div>

        {/* Right side - User menu */}
        <div className="relative z-[9999]">
          <button
            ref={buttonRef}
            onClick={() => setShowUserMenu(!showUserMenu)}
            className="flex items-center space-x-3 bg-black/30 rounded-lg px-4 py-2 text-white hover:bg-black/40 transition-colors relative z-[10000]"
          >
            <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center text-sm font-bold">
              {user?.username?.charAt(0).toUpperCase() || 'U'}
            </div>
            <div className="text-left hidden sm:block">
              <p className="text-sm font-medium">{user?.name || user?.username}</p>
              <p className="text-xs text-purple-300">
                {user?.role === 'admin' ? 'üëë Admin' : 'üë§ User'}
                {user?.is_verified && ' ‚úÖ'}
              </p>
            </div>
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Render dropdown as portal */}
          {showUserMenu && createPortal(<DropdownMenu />, document.body)}
        </div>
      </div>
    </div>
  );
};

export default Header;
